// Get the output array from the input
const sectors = $input.first().json.output;

// Array to store all messages
const messages = [];

// Loop through sectors and companies
sectors.forEach(sector => {
  sector.companies.forEach(company => {  
    let message;

    if(+company.rank === 1){
      message = `üìä **${sector.sector}**\n\n${company.rank}. **${company.company_name}** (${company.ticker})\n${company.investment_thesis}\n\nüèÜ **Sector Winner:** ${sector.sector_winner}\n\n${'='.repeat(50)}`;
    }
    else{
      message = `üìä **${sector.sector}**\n\n${company.rank}. **${company.company_name}** (${company.ticker})\n${company.investment_thesis}\n\n${'='.repeat(50)}`;
    }

    messages.push({
      json: {
        message: message,
        sector: sector.sector,
        company: company.company_name,
        ticker: company.ticker,
        rank: company.rank
      }
    });
  });
});

// Return array of items
return messages;
